import React, {useState, useEffect} from "react";
import { View, StyleSheet, TouchableWithoutFeedback } from "react-native";
import Swipeable from "react-native-gesture-handler/Swipeable";
import moment from 'moment';
import { Image } from "react-native-expo-image-cache";

import Text from "./Text";

import colors from "../config/colors";


function Card({ title, subTitle, imageUrl, onPress, thumbnailUrl, renderRightActions }) {

	/*const [currentDate, setCurrentDate] = useState('');

	useEffect(()=>{
		var date = new Date().getDate();
		setCurrentDate(date);
	}, []) */

	const Timer = moment().calendar();

	return (
		<Swipeable renderRightActions={renderRightActions}>
		<TouchableWithoutFeedback onPress={onPress}>
			<View style={styles.card}>
				<Image
					style={styles.image}
					tint="light"
					preview={{ uri: thumbnailUrl }}
					uri={imageUrl}
				/>

				<View style={styles.detailsContainerLeft}>
					<Text style={styles.title} numberOfLines={1}>
						{title}
					</Text>
					<Text style={styles.subTitle} numberOfLines={3}>
						{subTitle}
					</Text>
					<Text>Likes</Text>
				</View>
				<View style={styles.detailsContainerRight} >
					<Text style={styles.time} >{Timer}</Text>
				</View>
			</View>
		</TouchableWithoutFeedback>
		</Swipeable>
	);
}

const styles = StyleSheet.create({
	card: {
		borderRadius: 15,
		backgroundColor: colors.white,
		marginBottom: 20,
		overflow: "hidden",
	},
	detailsContainerLeft: {
		padding: 20,
	},
	detailsContainerRight:{
		position:'absolute',
		right:20,
		bottom:20
	},
	time:{
		color:'grey'
	},
	image: {
		width: "100%",
		height: 200,
	},
	title: {
		color: colors.secondary,
		fontWeight: "bold",
		marginBottom:7,
	},
	subTitle: {
		marginBottom: 7,
	},
});

export default Card;
