import React, { useState } from "react";
import { StyleSheet } from "react-native";
import * as Yup from "yup";
import CategoryPickerItem from "../components/CategoryPickerItem";
import Screen from "../components/Screen";
import {
	ErrorMessage,
	Form,
	FormField,
	FormPicker,
	SubmitButton,
} from "../components/forms";
import ActivityIndicator from "../components/ActivityIndicator";

import authApi from "../api/auth";
import useAuth from "../auth/useAuth";
import useApi from "../hooks/useApi";
import usersApi from "../api/users";

const validationSchema = Yup.object().shape({
	accountType: Yup.object().required().label("AccountType"),
	name: Yup.string().required().label("Name"),
	email: Yup.string().required().email().label("Email"),
	password: Yup.string().required().min(4).label("Password"),
});

function RegisterScreen() {
	const registerApi = useApi(usersApi.register);
	const loginApi = useApi(authApi.login);
	const auth = useAuth();
	const [error, setError] = useState();

	const accountTypes = [
		{
			backgroundColor: "#00b0f0",
			icon: "account",
			label: "Particulier",
			value: 1,
		},
		{
			backgroundColor: "#00f0b0",
			icon: "account-cash",
			label: "Professionnel",
			value: 2,
		},
	]

	const handleSubmit = async (userInfo) => {
		const result = await registerApi.request(userInfo);

		if (!result.ok) {
			if (result.data) setError(result.data.error);
			else {
				setError("An unexpected error occurred.");
				console.log(result);
			}
			return;
		}

		const { data: authToken } = await loginApi.request(
			userInfo.email,
			userInfo.password
		);
		auth.logIn(authToken);
	};

	return (
		<>
			<ActivityIndicator
				visible={registerApi.loading || loginApi.loading}
			/>
			<Screen style={styles.container}>
				<Form
					initialValues={{ accountType: "", name: "", email: "", password: "" }}
					onSubmit={handleSubmit}
					validationSchema={validationSchema}
				>
					<ErrorMessage error={error} visible={error} />
					<FormPicker
						name="accountType"
						placeholder="Type de compte" //liste dÃ©roulante
						items={accountTypes}
						PickerItemComponent={CategoryPickerItem}


					/>
					<FormField
						autoCorrect={false}
						icon="account"
						name="name"
						placeholder="Nom"
					/>
					<FormField
						autoCapitalize="none"
						autoCorrect={false}
						icon="email"
						keyboardType="email-address"
						name="email"
						placeholder="Email                  "
						textContentType="emailAddress"
					/>
					<FormField
						autoCapitalize="none"
						autoCorrect={false}
						icon="lock"
						name="password"
						placeholder="Mot de passe"
						secureTextEntry
						textContentType="password"
					/>
					<SubmitButton title="Inscription" />
				</Form>
			</Screen>
		</>
	);
}

const styles = StyleSheet.create({
	container: {
		padding: 10,
	},
});

export default RegisterScreen;
